-- ----------------------------
-- Procedure structure for PROC_QRY_CLS
-- ----------------------------
DROP PROCEDURE IF EXISTS `PROC_QRY_CLS`;
delimiter ;;
CREATE PROCEDURE `hznu`.`PROC_QRY_CLS`(IN `data` varchar(20000))
BEGIN
	DECLARE _uid  varchar(20) 	default null;
	SET _uid = JSON_UNQUOTE(JSON_EXTRACT(data,'$.uid'));
	
	select bname as name,GROUP_CONCAT(code order by code) as code from (
		select distinct(bname),code from tab_tech_main where uid = _uid order by name,code
	) t group by bname;
	
END
;;
delimiter ;

