-- ----------------------------
-- Procedure structure for PROC_SAV_CLS
-- ----------------------------
DROP PROCEDURE IF EXISTS `PROC_SAV_CLS`;
delimiter ;;
CREATE PROCEDURE `hznu`.`PROC_SAV_CLS`(IN `data` varchar(20000))
BEGIN
	DECLARE finished INTEGER DEFAULT 0;
	DECLARE _uid  varchar(20) 	default null;
	DECLARE _code varchar(20) 	default null;
	DECLARE _id int DEFAULT 0;
	DECLARE _t_hour int DEFAULT 0;
	DECLARE _e_hour int DEFAULT 0;
	DECLARE _web int DEFAULT 0;
	DECLARE _s_tech varchar(20) 	default null;
	DECLARE _q_time varchar(20) 	default null;
	DECLARE _q_addr varchar(20) 	default null;
	
	DECLARE _url    varchar(20) 	default null;
	DECLARE _click  varchar(20) 	default null;
	DECLARE _usr    varchar(20) 	default null;
	DECLARE _pwd    varchar(20) 	default null;
	
	DECLARE _desc   varchar(200) 	default null;
	DECLARE _mate   varchar(200) 	default null;
	DECLARE _exam   varchar(200) 	default null;
	DECLARE _method varchar(200) 	default null;

	
	DECLARE rs1 CURSOR FOR select id from tab_tech_main t where FIND_IN_SET(`code`, _code) and uid = _uid;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1;

	SET _uid  = JSON_UNQUOTE(JSON_EXTRACT(data,'$.uid'));
	SET _code = JSON_UNQUOTE(JSON_EXTRACT(data,'$.code'));
	SET _t_hour = CONVERT(JSON_EXTRACT(data, '$.t_hour'), UNSIGNED);
	SET _e_hour = CONVERT(JSON_EXTRACT(data, '$.e_hour'), UNSIGNED);
	
	SET _s_tech = JSON_UNQUOTE(JSON_EXTRACT(data,'$.s_tech'));
	SET _q_time = JSON_UNQUOTE(JSON_EXTRACT(data,'$.q_time'));
	SET _q_addr = JSON_UNQUOTE(JSON_EXTRACT(data,'$.q_addr'));
	
	
	SET _web    = CONVERT(JSON_EXTRACT(data, '$.web'), UNSIGNED);
	SET _url    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.url'));
	SET _click  = JSON_UNQUOTE(JSON_EXTRACT(data,'$.click'));
	SET _usr    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.usr'));
	SET _pwd    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.pwd'));
-- 	
-- 	call DEBUG_MSG(_web);
-- 	call DEBUG_MSG(_uid);
-- 	call DEBUG_MSG(_code);
	
	SET _desc    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.desc'));
	SET _mate    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.mate'));
	SET _exam    = JSON_UNQUOTE(JSON_EXTRACT(data,'$.exam'));
	SET _method  = JSON_UNQUOTE(JSON_EXTRACT(data,'$.method'));
	
	
	
  open rs1;
  st: LOOP
    fetch rs1 into _id;
    IF finished=1 THEN LEAVE st;  END IF; 
		
		update 
			tab_tech_main 
		set 
			t_hour = _t_hour, e_hour=_e_hour,s_tech=_s_tech,q_time=_q_time,q_addr=_q_addr,
			web=_web,url=_url,click=_click,usr=_usr,pwd=_pwd,
			`desc`=_desc,mate=_mate,exam=_exam,method=_method
		where uid=_uid and FIND_IN_SET(`code`, _code) ;
    
  END LOOP st;
  CLOSE rs1;
	
	select * from tab_tech_main t where FIND_IN_SET(`code`, _code) and uid = _uid;
	
END
;;
delimiter ;

