DROP PROCEDURE IF EXISTS `PROC_SAV_EXP`;
delimiter ;;
CREATE PROCEDURE `hznu`.`PROC_SAV_EXP`(IN `data` varchar(20000))
BEGIN
	DECLARE _len   int UNSIGNED;
	DECLARE _index int UNSIGNED DEFAULT 0;
	DECLARE _uid    varchar(20) 	default null;
	DECLARE _code   varchar(20) 	default null;
	DECLARE _list varchar(20000) default null;
	DECLARE _name    varchar(20) 	default null;
	DECLARE _type  varchar(20) 	default null;
	DECLARE _prop  varchar(20) 	default null;
	DECLARE _addr  varchar(50) 	default null;
	DECLARE _gnum  varchar(20) 	default null;
	
	

	SET _uid  = JSON_UNQUOTE(JSON_EXTRACT(data,'$.uid'));
	SET _code = JSON_UNQUOTE(JSON_EXTRACT(data,'$.code'));
	SET _list = JSON_UNQUOTE(JSON_EXTRACT(data,'$.expList'));
	set _len  = JSON_LENGTH(_list);
	
	delete from tab_tech_ep where code=_code and uid = _uid;
	
	while `_index` < `_len` do
		 set _name = JSON_UNQUOTE(json_extract(`_list`, concat('$[', `_index`, '].name')));
		 set _type = JSON_UNQUOTE(json_extract(`_list`, concat('$[', `_index`, '].type')));
		 set _prop = JSON_UNQUOTE(json_extract(`_list`, concat('$[', `_index`, '].prop')));
		 set _addr = JSON_UNQUOTE(json_extract(`_list`, concat('$[', `_index`, '].addr')));
		 set _gnum = JSON_UNQUOTE(json_extract(`_list`, concat('$[', `_index`, '].gnum')));
		 set `_index` := `_index` + 1;
	end while;
	
	
	select * from tab_tech_ep t where code=_code and uid = _uid;
END
;;
delimiter ;

